--
-- 資料表格式： `product`
--

CREATE TABLE IF NOT EXISTS `product` (
  `p_id` varchar(10) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `c_id` int(11) NOT NULL DEFAULT '0',
  `p_name` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `p_info` text COLLATE utf8_unicode_ci NOT NULL,
  `p_source` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_cost` int(11) NOT NULL DEFAULT '0',
  `p_price` int(11) NOT NULL DEFAULT '0',
  `p_sold` int(11) NOT NULL DEFAULT '0',
  `p_stocks` int(11) NOT NULL DEFAULT '0',
  `p_link` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_pic` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `p_active` tinyint(1) NOT NULL DEFAULT '0',
  `p_hot` int(11) NOT NULL DEFAULT '0',
  `p_recommend` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`p_id`),
  UNIQUE KEY `p_name` (`p_name`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='產品資料表';

--
-- 列出以下資料庫的數據： `product`
--

INSERT INTO `product` (`p_id`, `c_id`, `p_name`, `p_info`, `p_source`, `p_cost`, `p_price`, `p_sold`, `p_stocks`, `p_link`, `p_pic`, `p_date`, `p_active`, `p_hot`, `p_recommend`) VALUES
('F900', 4, 'PHP 網頁模組隨學隨用', '1. 網站功能範例多，各模組功能完備、非坊間陽春版的模組可比。\r\n2. 可觀摩商業及模組的動態開發技巧。\r\n3. 可直接將模組引用至現有網站，立即提昇網站功能。\r\n4. 幫助 PHP 架站者打造風格獨具、獨一無二的網站。\r\n5. 收錄統計圖表、網路相簿等不易於他書見到的功能。 ', 'Flag', 336, 480, 66, 35, 'http://www.flag.com.tw/book/5105.asp?bokno=F900', 'F900.gif', '2009-01-10 10:03:26', 0, 28, 1),
('FS480', 4, '最新 PHP + MySQL + Ajax 網頁程式設計', '不只是學語法, 更讓您能快速寫出具備所需功能的網頁程式！\r\n\r\n本書從基本觀念教起, 透過精心設計的語法及函式教學, 帶您快速建立 PHP 必備的網頁程式設計基礎。接著採取由做中學的方式, 透過可直接使用的應用範例, 以淺顯易懂的圖例, 清晰詳盡的程式註解, 說明程式與網頁的運作流程、特性。讓您在學習的同時, 也完成一個個可實際視線運作的網頁程式。\r\n\r\n另外, 本書囊括 AJAX、PEAR 函式庫及 Smarty 樣版等熱門技術, 並事實提供各項安全叮嚀, 即使是初學者, 也可以寫出安全、且具備最新功能的網頁程式。 ', '旗標出版有限公司', 455, 650, 232, 87, 'http://www.flag.com.tw/book/5105.asp?bokno=FS480', 'FS480.gif', '2008-12-10 10:54:03', 1, 188, 1),
('FT305', 4, 'Ubuntu Linux 強效應用', 'Linux 不但可以輕鬆學, 更可以有趣又實用！\r\n不用複雜的指令, 更無須高手段數, Windows 的實用, Ubuntu 也可以做到！', 'Flag', 305, 450, 50, 105, 'http://www.flag.com.tw/book/5105.asp?bokno=FT305', 'FT305.gif', '2009-01-12 06:55:57', 1, 46, 1),
('FT603', 4, 'PCDIY 2009 電腦選購、組裝、應用', '想買新電腦, 但擔心買貴了？功能不足？或等級太低嗎？唯有了解零組件的差異, 才能用最便宜的價格, 買到保證滿意的電腦！\r\n\r\n這是一本最簡單易懂的電腦硬體入門書, 擁有零組件最新規格、特色說明, 及大量精美的照 (圖) 片輔文, 強調『一圖一步驟』的全圖解式安裝操作, 是所有想買 (組) 電腦、想認識 PC 硬體規格的不二選擇！ ', 'Flag', 400, 450, 36, 300, 'http://www.flag.com.tw/book/5105.asp?bokno=FT603', 'FT603.gif', '2009-01-12 06:57:52', 1, 64, 1),
('FT221', 4, 'Microsoft SQL Server 2008 管理實務', '本書以 SQL Server 2008 管理應用為主題, 內容涵蓋管理員在管理 SQL Server 2008 時較常接觸的各項工作：從伺服器安裝, 到初期的安全性管理, 乃至經常性的資料庫備份、複寫、效能調效、建立作業等維護工作, 包含原則管理、備份壓縮、安全性稽核 (SQL Server Audit) 等數項 SQL Server 2008 實用的新功能。此外, 書中適時提供實務上的建議及注意事項, 相信能幫助初次接觸 SQL Server 2008 管理的讀者, 更快進入狀況。 ', '旗標', 490, 680, 83, 109, 'http://www.flag.com.tw/book/5105.asp?bokno=FT221', 'FT221.gif', '2009-01-13 09:05:17', 0, 25, 1),
('F9300', 4, 'Fedora 10 Linux 實務應用', '本書以最新 Fedora 10 為操作平台, 從建立 Linux 基本觀念、安裝系統、X Window 環境、文字模式指令、中文系統、Internet 上網、OpenOffice.org 辦公室軟體、各種應用程式、帳號管理、檔案權限設定到架設伺服器, 全部完整介紹！', '旗標', 450, 650, 180, 62, 'http://www.flag.com.tw/book/5105.asp?bokno=F9300', 'F9300.gif', '2009-01-14 11:44:37', 0, 52, 1),
('FS011', 4, 'Excel 2007 資料分析達人講座', '【學會善用 Excel 分析工具, 察覺數字背後隱藏的真相, 才能做出準確的決策！】\r\n\r\n多數上班族雖然常常使用Excel, 不過多半只會輸入資料、調整外觀等皮毛, 熟練一點的人也頂多會利用簡單的函數進行加總、統計, 因此每當遇到大量資料需要做整理分析時, 只能土法煉鋼佔去大量時間, 而且很容易出錯。\r\n\r\n其實 Excel 提供許多分析工具可以快速完成這類資料整理、分析的工作, 特別是公司的行銷人員與主管常需要從大量報表過濾出有用的資訊, 跟著本書學會使用 Excel 分析工具, 將可事半功倍！ ', '旗標', 350, 520, 231, 99, 'http://www.flag.com.tw/book/5105.asp?bokno=FS011', 'FS011.gif', '2009-01-14 11:16:17', 0, 29, 0),
('FT623', 4, '一定更漂亮! Photoshop 人像編修', '本書會教你先將光線、色彩等基本問題修好, 然後再針對各種現實生活中最令人在意的容貌、體態問題做修補, 像是局部抽脂、削髖骨、小蠻腰、柔嫩膚質、改頭換面之韓式整形…應有盡有。接著再透過修片的手法, 看是要讓主角周遊世界名勝, 或者打盞鎂光燈、化身為超級名模, 甚至是變身為雜誌封面主角、編排多張照片, 並加上漫畫般的對白…保證你一玩停不了呢！ ', '旗標', 290, 420, 187, 47, 'http://www.flag.com.tw/book/5105.asp?bokno=FT623', 'FT623.gif', '2009-01-14 11:25:00', 0, 34, 1),
('FT651', 4, 'Michael Freeman的私家攝技 - 絕活101', '想要邁向專業, 突破攝影瓶頸, 最快的方式就是吸收名師的工作經驗。但多少人能有這樣的機會？現在透過數位攝影大師 Michael Freeman所彙整的攝影技巧, 就好像名師在旁親授一樣, 能幫助您突破自我, 增進數甲子的攝影功力！ ', '旗標', 580, 550, 108, 26, 'http://www.flag.com.tw/book/5105.asp?bokno=FT651', 'FT651.gif', '2009-01-14 11:27:26', 1, 48, 1),
('F9650', 4, '突破傳統！戰勝極限的數位攝影專家技法', '『沒有光，就沒有攝影』，光線是攝影者的畫筆，掌握光影才能捕捉剎那間的感動！\r\n\r\n然而，很多人都只注意到「光」，卻常常忽略了「影」（暗部），一旦場景光線不夠明亮時，就覺得不好拍、也拍不好！完美的攝影不僅要掌握光、更要能處理影；若能靈活發揮手邊的工具、設備、與技術，任何環境都能拍出充滿創意的作品。', '旗標', 410, 580, 136, 46, 'http://www.flag.com.tw/book/5105.asp?bokno=F9650', 'F9650.gif', '2009-01-14 11:29:05', 0, 60, 1),
('FT416', 4, 'Web+ 設計的黃金則 - XHTML + CSS 虎之卷', '要想做出好的網頁設計, CSS 與 XHTML 已不是加分的選項技術, 而是非會不可的必修技法. 如果只懂得 CSS 的皮毛, 就無法解決實務上遇到的各種問題. \r\n\r\n日本網頁設計達人大藤幹, 根據多年經驗, 分成「基本規則篇」、「實務製作篇」、「常見疑問與解答篇」、「困難解決實戰篇」 四個重要的主題, 整理出 CSS 網頁設計黃金則。並從製作的「原因」、「想法」開始解說, 再提出可能的「對策」以及如何選擇「恰當的手法」來解決 CSS 設計上的問題。', '旗標', 405, 580, 87, 76, 'http://www.flag.com.tw/book/5105.asp?bokno=FT416', 'FT416.gif', '2009-01-14 11:31:59', 0, 31, 1),
('007', 4, 'James Bond', '', 'MI6', 5000000, 10000000, 0, 1, '', '', '2009-01-14 11:48:35', 1, 105, 0);

